document.addEventListener("DOMContentLoaded",function(){[{selector:".sqs-heading-font",variable:"--heading-font-font-family"},{selector:".sqs-body-font",variable:"--body-font-font-family"}].forEach(({selector:e,variable:t})=>{let o=document.querySelector(e);if(o){let n=getComputedStyle(o).fontFamily;document.documentElement.style.setProperty(t,n)}})});